const BoldRenderer = ({ text }: { text: string }) => {
    const parts = text.split('**');
    return (
        <>
            {parts.map((part, i) =>
                i % 2 === 1 ? <strong key={i} className="font-bold">{part}</strong> : part
            )}
        </>
    );
};

